import React from 'react';
import { Container,Row,Col,Form ,Button} from 'react-bootstrap';
import {connect} from 'react-redux';
const axios = require('axios');

class UserProfile extends React.Component {
    constructor(props){
        super(props);
        this.state={
            user_id:this.props.user_id,
            username:this.props.firstName,
            // email:this.props.email,
            // profileImage:this.props.profileImage,
            // msg:this.props.msg,
            // uploadedFile:null
        }
    }
    fetchUserDetails=(user_id)=>{
      //console.log(user_id);
      axios.get("http://localhost:2000/api/getUserDetails/"+user_id,{
          headers: {
              "content-type": "application/json"
            }
      }).then(res=>{
          console.log(res);
          this.setState({email:res.data.results[0].email});
          this.setState({profileImage:res.data.results[0].profileImage})
      })
      .catch(err=>console.log(err))
  }
    render(){
    return (
        <Container>
        <Row>
       <Col>
       {/* <img  alt="profils pic" /> */}
       </Col>
        <Col>
            <h1>User Profile</h1>
            <Form className="form">     
    <p>{this.state.msg}</p>
  <Form.Group controlId="formCategory1">
    <Form.Label>firstName</Form.Label>
    <Form.Control type="text" defaultValue={this.state.firstName}/>
  </Form.Group>

  {/* <Form.Group controlId="formCategory2">
    <Form.Label>Email</Form.Label>
    <Form.Control type="email" defaultValue={this.state.email} />
  </Form.Group>
  */}
  {/* <Form.Group controlId="formCategory4">
    <Form.Label>Profile Image</Form.Label>
    <Form.Control type="file" name="profileImage" />
    </Form.Group> */}

  <Button variant="primary">Update Profile</Button>
  </Form>
   </Col>

       </Row>
        </Container>
    )
}
}

const mapStatetoProps=(state)=>{
    return{
        user_id:state.user._id,
        username:state.user.firstName,
    //    email:state.user.email,
    //    profileImage: state.user.profileImage,
    //    msg:state.user.msg
    }
   }
   
   

   export default connect(mapStatetoProps)(UserProfile);
